import{_ as o,c as t,o as a,ag as i}from"./chunks/framework.KQPf6zax.js";const p=JSON.parse('{"title":"Scrust Syntax Guide","description":"","frontmatter":{},"headers":[],"relativePath":"guide/syntax/index.md","filePath":"guide/syntax/index.md"}'),s={name:"guide/syntax/index.md"};function r(l,e,n,c,d,h){return a(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="scrust-syntax-guide" tabindex="-1">Scrust Syntax Guide <a class="header-anchor" href="#scrust-syntax-guide" aria-label="Permalink to &quot;Scrust Syntax Guide&quot;">​</a></h1><p>Scrust provides a modern syntax that compiles directly to Scratch blocks. This guide covers every aspect of the language, from basic variables to complex control flow.</p><h2 id="design-philosophy" tabindex="-1">Design Philosophy <a class="header-anchor" href="#design-philosophy" aria-label="Permalink to &quot;Design Philosophy&quot;">​</a></h2><p>Scrust is designed to feel familiar to Rust developers while mapping 1:1 to Scratch&#39;s logic.</p><ul><li><strong>Explicit Visibility</strong>: Variables are <code>private</code> (For this sprite only) or <code>public</code> (For all sprites) by default.</li><li><strong>Structured Flow</strong>: No more dangling blocks. <code>if</code>, <code>loop</code>, and functions provide structure.</li><li><strong>Block Stacks</strong>: Separate groups of blocks with blank lines to create multiple scripts.</li><li><strong>Asset Integration</strong>: Define your costumes and sounds right where you use them.</li></ul><h2 id="table-of-contents" tabindex="-1">Table of Contents <a class="header-anchor" href="#table-of-contents" aria-label="Permalink to &quot;Table of Contents&quot;">​</a></h2><ul><li><a href="./variables.html">Variables &amp; Lists</a> - Managing state with <code>var</code> and <code>list</code>.</li><li><a href="./procedures.html">Procedures</a> - Creating custom blocks (&quot;My Blocks&quot;).</li><li><a href="./packages.html">Packages</a> - Reusing code with libraries and dependencies.</li><li><a href="./events.html">Events</a> - Handling flag clicks, key presses, and more.</li><li><a href="./control-flow.html">Control Flow</a> - Loops (<code>forever</code>, <code>repeat</code>), Conditionals (<code>if/else</code>), and Matching (<code>match</code>).</li><li><a href="./operators.html">Operators</a> - Math, logic, and string manipulation.</li><li><a href="./blocks.html">Standard Blocks</a> - Motion, Looks, Sound, and Sensing commands.</li></ul>',7)])])}const g=o(s,[["render",r]]);export{p as __pageData,g as default};
